import { useQuery } from '@tanstack/react-query'

import fetchExtended from '@/configs/fetch/fetch-extend'

import { ErrorResponseDTOCommonType } from '../@types/data-contracts'
import {
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { BizApiApi } from './BizApi.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const bizApiApi = new BizApiApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_BIZ_API_API = {
  GET_RELATED_BIZ: (variables?: Parameter<typeof bizApiApi.getRelatedBiz>) =>
    ['GET_RELATED_BIZ', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags [Biz API]
 * @name GetRelatedBiz
 * @summary 비즈 카테고리별 연관 콘텐츠 반환
 * @request GET:/api/v1/biz/related
 * @secure    */

export const useGetRelatedBizQuery = <
  TData = RequestFnReturn<typeof bizApiApi.getRelatedBiz>,
>(
  params: QueryHookParams<
    typeof bizApiApi.getRelatedBiz,
    { error: ErrorResponseDTOCommonType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_BIZ_API_API.GET_RELATED_BIZ(params.variables)
  return useQuery({
    queryKey,
    queryFn: () => bizApiApi.getRelatedBiz(params.variables),
    ...params?.options,
  })
}
