import { useQuery } from '@tanstack/react-query'

import fetchExtended from '@/configs/fetch/fetch-extend'

import { ErrorResponseDTOCommonType } from '../@types/data-contracts'
import {
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { FaqApiApi } from './FaqApi.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const faqApiApi = new FaqApiApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_FAQ_API_API = {
  GET_ALL_FAQ: (variables?: Parameter<typeof faqApiApi.getAllFaq>) =>
    ['GET_ALL_FAQ', variables].filter(isDefined),
}

/**
 * @description ## FAQ 질문 조회 - isTop -> true 인 경우, 무조건 TOP/FAQ 로 조회 - category -> isTop의 값이 false인 경우 조회 / isTop 이 false인데 전달되지 않으면 'SERVICE'를 디폴트로 함
 *
 * @tags [FAQ API]
 * @name GetAllFaq
 * @summary FAQ 질문 조회
 * @request GET:/api/v1/faq
 * @secure    */

export const useGetAllFaqQuery = <
  TData = RequestFnReturn<typeof faqApiApi.getAllFaq>,
>(
  params?: QueryHookParams<
    typeof faqApiApi.getAllFaq,
    { error: ErrorResponseDTOCommonType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_FAQ_API_API.GET_ALL_FAQ(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => faqApiApi.getAllFaq(params?.variables),
    ...params?.options,
  })
}
