import { useQuery } from '@tanstack/react-query'

import fetchExtended from '@/configs/fetch/fetch-extend'

import { ErrorResponseDTOCommonType } from '../@types/data-contracts'
import {
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { HomeApiApi } from './HomeApi.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const homeApiApi = new HomeApiApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_HOME_API_API = {
  GET_SEARCH_RESPONSE: (
    variables?: Parameter<typeof homeApiApi.getSearchResponse>,
  ) => ['GET_SEARCH_RESPONSE', variables].filter(isDefined),
  GET_RANDOM_SEARCH_KEYWORD: (
    variables?: Parameter<typeof homeApiApi.getRandomSearchKeyword>,
  ) => ['GET_RANDOM_SEARCH_KEYWORD', variables].filter(isDefined),
  GET_SEARCH_KEYWORD_AUTO_COMPLETE: (
    variables?: Parameter<typeof homeApiApi.getSearchKeywordAutoComplete>,
  ) => ['GET_SEARCH_KEYWORD_AUTO_COMPLETE', variables].filter(isDefined),
  GET_HOME_CONFIG_HELP_ARTICLE: (
    variables?: Parameter<typeof homeApiApi.getHomeConfigHelpArticle>,
  ) => ['GET_HOME_CONFIG_HELP_ARTICLE', variables].filter(isDefined),
  GET_HOME_EXPERT: (variables?: Parameter<typeof homeApiApi.getHomeExpert>) =>
    ['GET_HOME_EXPERT', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags [Home API]
 * @name GetSearchResponse
 * @summary 홈 - 검색 기능
 * @request GET:/api/v1/home/search
 * @secure    */

export const useGetSearchResponseQuery = <
  TData = RequestFnReturn<typeof homeApiApi.getSearchResponse>,
>(
  params: QueryHookParams<
    typeof homeApiApi.getSearchResponse,
    { error: ErrorResponseDTOCommonType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_HOME_API_API.GET_SEARCH_RESPONSE(params.variables)
  return useQuery({
    queryKey,
    queryFn: () => homeApiApi.getSearchResponse(params.variables),
    ...params?.options,
  })
}

