import { useQuery } from '@tanstack/react-query'

import fetchExtended from '@/configs/fetch/fetch-extend'

import { ErrorResponseDTOCommonType } from '../@types/data-contracts'
import {
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { SeoApiApi } from './SeoApi.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const seoApiApi = new SeoApiApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_SEO_API_API = {
  GET_SITE_SEO_INFO: (variables?: Parameter<typeof seoApiApi.getSiteSeoInfo>) =>
    ['GET_SITE_SEO_INFO', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags [Seo API]
 * @name GetSiteSeoInfo
 * @summary 사이트 SEO
 * @request GET:/api/v1/seo
 * @secure    */

export const useGetSiteSeoInfoQuery = <
  TData = RequestFnReturn<typeof seoApiApi.getSiteSeoInfo>,
>(
  params?: QueryHookParams<
    typeof seoApiApi.getSiteSeoInfo,
    { error: ErrorResponseDTOCommonType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_SEO_API_API.GET_SITE_SEO_INFO(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => seoApiApi.getSiteSeoInfo(params?.variables),
    ...params?.options,
  })
}
