import { useQuery } from '@tanstack/react-query'

import fetchExtended from '@/configs/fetch/fetch-extend'

import { ErrorResponseDTOCommonType } from '../@types/data-contracts'
import {
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { SolutionApiApi } from './SolutionApi.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const solutionApiApi = new SolutionApiApi({
  customFetch: fetchExtended,
})

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_SOLUTION_API_API = {
  GET_RELATED_SOLUTION: (
    variables?: Parameter<typeof solutionApiApi.getRelatedSolution>,
  ) => ['GET_RELATED_SOLUTION', variables].filter(isDefined),
  GET_SOLUTION_EXPERT_LIST: (
    variables?: Parameter<typeof solutionApiApi.getSolutionExpertList>,
  ) => ['GET_SOLUTION_EXPERT_LIST', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags [Solution API]
 * @name GetRelatedSolution
 * @summary 솔루션별 연관 콘텐츠 반환
 * @request GET:/api/v1/solution/related
 * @secure    */

export const useGetRelatedSolutionQuery = <
  TData = RequestFnReturn<typeof solutionApiApi.getRelatedSolution>,
>(
  params: QueryHookParams<
    typeof solutionApiApi.getRelatedSolution,
    { error: ErrorResponseDTOCommonType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_SOLUTION_API_API.GET_RELATED_SOLUTION(
    params.variables,
  )
  return useQuery({
    queryKey,
    queryFn: () => solutionApiApi.getRelatedSolution(params.variables),
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags [Solution API]
 * @name GetSolutionExpertList
 * @summary 솔루션별 전문가 리스트 반환
 * @request GET:/api/v1/solution/expert
 * @secure    */

export const useGetSolutionExpertListQuery = <
  TData = RequestFnReturn<typeof solutionApiApi.getSolutionExpertList>,
>(
  params: QueryHookParams<
    typeof solutionApiApi.getSolutionExpertList,
    { error: ErrorResponseDTOCommonType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_SOLUTION_API_API.GET_SOLUTION_EXPERT_LIST(
    params.variables,
  )
  return useQuery({
    queryKey,
    queryFn: () => solutionApiApi.getSolutionExpertList(params.variables),
    ...params?.options,
  })
}
